<?phpclass M_Mon_profil extends CI_Model{    public function modifier($data,$id){        $this->db->where('user_id',$id);		        $this->db->update('users',$data);    }    public function getIdMembre($data){        $this->db->select('*');        $this->db->from('users');        $this->db->where('user_id',$data);        $query = $this->db->get();        return $query->row();    }}